// Function to check if input matches with OTP
// params {input} list of entered number
// params {otp} generated otp
function checkOTP(input, otp) {
	if (input && input.length === 6 && otp >= 100000 && otp < 10000000) {
		const enteredOTP = input.join('');
		return enteredOTP === otp;
	}
}
